mapper.insert:
insert into article
(company,	title, summary, newspaper, publicationDate, url, image)
values(#{company}, #{title}, #{summary}, #{newspaper},		#{publicationDate}, #{url}, #{image});

mapper.update:
update article set
company=#{company}, title=#{title}, summary=#{summary},
newspaper=#{newspaper}, publicationDate=#{publicationDate},
url=#{url}, image=#{image}
where
articleid=#{articleid};
update article_content set
content=#{content}
where
articleid=#{articleid};
update article_keyword set
keyword=#{keyword}
where
articleid=#{articleid};
