mapper.insert:
insert into Article
(company,
title, summary, newspaper, publicationDate, url, image)
values(#{company}, #{title}, #{summary}, #{newspaper},
#{publicationDate}, #{url}, #{image});
insert into Article_content (content) values (#{content});
insert into Article_keyword (keyword) values (#{keyword});

mapper.update:
update Article set
company=#{company}, title=#{title}, summary=#{summary},
newspaper=#{newspaper}, publicationDate=#{publicationDate},
url=#{url}, image=#{image}
where
articleid=#{articleid};
update Article_content set
content=#{content}
where
articleid=#{articleid};
update Article_keyword set
keyword=#{keyword}
where
articleid=#{articleid};
